import '../App.css';
import axios from 'axios';
import React, { Component, useState } from 'react';
const FormData = require('form-data');

function Form() {

  const [selectedFile, setSelectedFile] = useState(null);
  const [title, setTitle] = useState(null);
  const [description, setDescription] = useState(null);

  const onFileChange = event => {
    this.setState({ selectedFile: event.target.files[0] });
  };

  const onTitleChange = event => {
    setTitle(event.target.value)
  }

  const onDescriptionChange = event => {
    this.setState({ title: event.target.value })
  }

  const onFileUpload = () => {
    const formData = new FormData();

    const pinFileToIPFS = (pinataApiKey, pinataSecretApiKey) => {
      const url = `https://api.pinata.cloud/pinning/pinFileToIPFS`;
      let data = new FormData();
      data.append('file', this.state.selectedFile);

      const metadata = JSON.stringify({
        name: 'testname',
        keyvalues: {
          exampleKey: 'exampleValue'
        }
      });
      data.append('pinataMetadata', metadata);

      const pinataOptions = JSON.stringify({
        cidVersion: 0,
        customPinPolicy: {
          regions: [
            {
              id: 'FRA1',
              desiredReplicationCount: 1
            },
            {
              id: 'NYC1',
              desiredReplicationCount: 2
            }
          ]
        }
      });
      data.append('pinataOptions', pinataOptions);

      axios
        .post(url, data, {
          maxBodyLength: 'Infinity', //this is needed to prevent axios from erroring out with large files
          headers: {
            'Content-Type': `multipart/form-data; boundary=${data._boundary}`,
            pinata_api_key: pinataApiKey,
            pinata_secret_api_key: pinataSecretApiKey
          }
        })
        .then(function (response) {
          console.log(response)
        })
        .catch(function (error) {
          console.log(error)
        });
    };
    pinFileToIPFS('aa687437416fab07fb57', 'd53a9d7f8e914d6063621f1534c3e4c353f690255e89621084d2073744c44f8a')
  };

  const fileData = () => {

    if (this.state.selectedFile) {

      return (
        <div>
          <h2>File Details:</h2>

          <p>File Name: {this.state.selectedFile.name}</p>


          <p>File Type: {this.state.selectedFile.type}</p>


          <p>
            Last Modified:{" "}
            {this.state.selectedFile.lastModifiedDate.toDateString()}
          </p>

        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h4>Choose before Pressing the Upload button</h4>
        </div>
      );
    }
  };

  return (
    <div>
      <h1>
        GeeksforGeeks
      </h1>
      <h3>
        File Upload using React!
      </h3>
      <div>
        <input type="file" onChange={this.onFileChange} />
        <label>Titre
          <input type="text" value={this.state.title} onChange={this.onTitleChange}></input>
        </label>
        <label>Description
          <input type="text" value={this.state.description} onChange={this.onDescriptionChange}></input>
        </label>
        <br />
        <button onClick={this.onFileUpload}>
          Upload!
        </button>
      </div>
      {this.fileData()}
    </div>
  );
}

export default Form;
