import './App.css';
import axios from 'axios';
import { NFTStorage, File } from 'nft.storage'
import { pack } from 'ipfs-car/pack';

import React, { Component } from 'react';

class App extends Component {
  state = {

    // Initially, no file is selected
    selectedFile: null
  };

  // On file select (from the pop up)
  onFileChange = event => {

    // Update the state
    this.setState({ selectedFile: event.target.files[0] });

  };

  // On file upload (click the upload button)
  onFileUpload = () => {

    // Create an object of formData
    const formData = new FormData();

    // Update the formData object
    formData.append(
      "myFile",
      this.state.selectedFile,
      this.state.selectedFile.name
    );

    // Details of the uploaded file
    console.log(this.state.selectedFile);

    // Request made to the backend api
    // Send formData object
    const uploadVideo = async () => {
      const apiKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiJkaWQ6ZXRocjoweDE3OTM1MjM2NTFFZDkzMUMyNUI3NkQ5OTEzY2U5MEM3OUNiOTRGNEEiLCJpc3MiOiJuZnQtc3RvcmFnZSIsImlhdCI6MTYzNjg4MTk2ODU3MiwibmFtZSI6Ik5GVElaIn0.jpi-SBEfCPVV4eJbKcXyIu4Whx5MgvJEVXFRgKLssfs'
      const data = formData
      const url = 'https://api.nft.storage/upload'

      const result = await fetch(url, {
        method: 'POST',
        headers: {
          'Authorization': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiJkaWQ6ZXRocjoweDE3OTM1MjM2NTFFZDkzMUMyNUI3NkQ5OTEzY2U5MEM3OUNiOTRGNEEiLCJpc3MiOiJuZnQtc3RvcmFnZSIsImlhdCI6MTYzNjg4MTk2ODU3MiwibmFtZSI6Ik5GVElaIn0.jpi-SBEfCPVV4eJbKcXyIu4Whx5MgvJEVXFRgKLssfs',
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: data,
      }).then((response) => {
        return response.json().then(function (json) {
          console.log('IPFS url: ', 'https://ipfs.io/ipfs/' + json.value.cid.toString());
          return 'https://ipfs.io/ipfs/' + json.value.cid.toString();
        })
          .catch(error => {
            throw (error)
          })
      });
    }
    uploadVideo();
  };

  // File content to be displayed after
  // file upload is complete
  fileData = () => {

    if (this.state.selectedFile) {

      return (
        <div>
          <h2>File Details:</h2>

          <p>File Name: {this.state.selectedFile.name}</p>


          <p>File Type: {this.state.selectedFile.type}</p>


          <p>
            Last Modified:{" "}
            {this.state.selectedFile.lastModifiedDate.toDateString()}
          </p>

        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h4>Choose before Pressing the Upload button</h4>
        </div>
      );
    }
  };

  render() {

    return (
      <div>
        <h1>
          GeeksforGeeks
        </h1>
        <h3>
          File Upload using React!
        </h3>
        <div>
          <input type="file" onChange={this.onFileChange} />
          <button onClick={this.onFileUpload}>
            Upload!
          </button>
        </div>
        {this.fileData()}
      </div>
    );
  }
}


export default App;
